$(document).ready(function() {
    $('.count-text').each(function() {
        $(this).prop('Counter', 0).animate({
            Counter: $(this).attr('data-stop')
        }, {
            duration: 2000, // You can adjust the duration of the animation
            easing: 'swing',
            step: function(now) {
                $(this).text(Math.ceil(now));
            }
        });
    });

    $('.count-text').waypoint({
        handler: function(direction) {
            if (direction === 'down') {
                $(this.element).animateNumber({
                    number: parseInt($(this.element).text())
                }, 5000);
            }
        },
        offset: '85%' // You can adjust the offset to trigger the animation
    });
});